import React, { useState } from 'react'
import './App.css'

const Counter = (props) => {

  return (
    <div>
            <div className={`custom-control custom-switch text-black ${(props.theme==='dark')? bg-dark : bg-light }`}>
              <input onClick={props.effect} type="checkbox" className="custom-control-input" id="customSwitch1" />
              <label className="custom-control-label" htmlFor="customSwitch1">{props.modename}</label>
            </div>

            <div className={(props.theme==='dark')?"App-header":"App-header1"}>
              <div className="conatainer">
                  <div className="child1">Name : Raj</div>
                  <div className="child2">Age : 20</div>
                  <div className="child3">Addr : Gkp</div>
                  <div className="child4">Mob no. : 1234 </div>
              </div>
            </div>
    </div>
  )}

export default Counter

























