import './App.css';
import React from 'react';

class App extends React.Component{

  constructor(){
    super()
    this.state ={
      count : 0
    }
    this.increase = this.increase.bind(this)
  }
  increase =(preState)=>{
   this.setState({
    count : this.preState.count +1
   });

  }

  render(){
  return (
    <>
      <div className="App-header">
        <div className="child1">
          <div className="countingResult">{this.state.count}</div>
          <hr />
          <div className="buttons">
            <button onClick={this.increase} className="positiveButton">Increase</button>
            <button className="negativeButton">Decrease</button>
          </div>
        </div>
      </div>
    </>
  );
}
}

export default App;
