import './App.css';
import Dark from './Dark';

const Counter = (props) => {


  return (
    <div>


            <div className={(props.theme==='dark')?"App-header":"App-header1"}>
              <div className={(props.modename==="dark")?"conatainer1":"conatainer"}>
                  <div className="question">
                    <h5>{`Q-${props.id} : ${props.question}`}</h5>
                    <h6>{`Ans : ${props.answer}`}</h6>
                    <hr />
                  </div>
              </div>
            </div>
    </div>
  )}

export default Counter

























