import React, {useState} from 'react';
import './App.css';

function Counter(){

    const [con , setCon]=useState('write something')
    const clickListen=()=>{
        let s = con.toUpperCase();
        setCon(s)
    }
    const changeHandler=(event)=>{
        let p = event.target.value
        setCon(p)
    }

    return(
        <>
            <form>
                <div className="head-data">
                    <strong>LogIn Form</strong>
                </div>
                    <br />
                <div className='if'>
                    <div>
                        <label htmlFor="">Username
                            <input type="text" />
                        </label>
                        <br />
                        <label htmlFor="">Password 
                            <input type="password" />
                        </label>
                    </div>
                </div>

            </form>
            <div className="btn">
                <button >Login</button>
            </div>

        </>
    )
}

export default Counter




















