import { display } from '@mui/system';
import React, {useState} from 'react';
import './App.css';

function Counter(){
    const [pass , setPass] = useState("")
    const [uname , setUname] = useState("")

    const showPass=(e)=>{
        let p = e.target.value
        setPass(p)
        console.log(p.length)
    }

    const showUserName =(e)=>{
        let pp = e.target.value
        setUname(pp)
    }

    return(
        <>
            <form>
                <div className="head-data">
                    <strong>LogIn Form</strong>
                </div>
                    <br />
                <div className='if'>
                    <div>
                        <label htmlFor="">Username : 
                            <input value={uname} onChange={showUserName} type="text" />
                        </label>

                        <br />

                        <label htmlFor="">Password :  
                            <input onChange={showPass} type="password" />
                        </label>

                        <br />

                        <small>{(pass.length>0)? (<>show password : {pass}</>): "" }  </small>
                    </div>
                </div>

            </form>
            <div className="btn">
                <button >Login</button>
            </div>
            <div className="if">
                {(uname.length>0 || pass.length>0 )? (<><small>UserName: {uname} , Password: {pass}</small></>) : "" }

            </div>

        </>
    )
}

export default Counter




















