import React from 'react';
import {makeStyles} from '@mui/styles';
import { Box, Container, Paper } from '@mui/material';
import Accordion from '@mui/material/Accordion';
import AccordionDetails from '@mui/material/AccordionDetails';
import AccordionSummary from '@mui/material/AccordionSummary';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import data from './QuestionApi';
import { useState } from 'react';

const useStyles = makeStyles(theme =>({
  accordion_details:{
    backgroundColor: 'red',
    textAlign:'center',

  },
  accordion:{
    marginBottom: theme.spacing(2),
    marginTop:theme.spacing(2)
  },

  summary:{
    marginTop: theme.spacing(5) ,
    backgroundColor: 'red',
  }

}));

const Dark = (props) => {
  const classes = useStyles()
  const [expend, setExpend]= useState(false)

  const expendHandler=()=>{
    setExpend(true)
  }

  return (

    <div className={classes.div}>
    <Container>
      <Accordion onClick={expendHandler} expanded={expend} className={classes.accordion}  >
        <AccordionSummary className={classes.summary} expandIcon={<ExpandMoreIcon />}> <strong> {`Q.${props.id}: `}</strong> {props.question} </AccordionSummary>
        <Paper>
        <hr />
        <AccordionDetails className={classes.accordion_details}>{`Ans :${props.answer}`}</AccordionDetails>
        </Paper>
      </Accordion>
      </Container>

    </div>
  )
}

export default Dark
