import React from 'react'
import './App.css'

const Dark = () => {
  return (
    <div>
        <div className={`custom-control custom-switch text-center text-black ${(props.theme==='dark')? "bg-dark" : "bg-light" }`}>
              <input onClick={props.effect} type="checkbox" className="custom-control-input" id="customSwitch1" />
              <label style={styles} className="custom-control-label" htmlFor="customSwitch1">{(props.modename==="dark")?(styles.color='white','Dark Mode'):(styles.color='black','Light Mode')}</label>
            </div>
    </div>
  )
}

export default Dark
