import React, { useState } from 'react';
import Counter from './Counter';
import QuestionApi from './QuestionApi';


function App() {
  const [mode, setMode]= useState('light');
  const effectFun=()=>{




    if(mode==='light'){
      setMode('dark')

      console.log('if clicked')
    }
    else(
      setMode('light')
    )

  }

  const qa = QuestionApi.map((e)=>{return <Counter id={e.id} question={e.question} answer={e.answer}/>})
  console.log(qa)


  return (
   <>
    {qa}
   </>
  )
}

export default App
