import React, { useState } from 'react'
import './App.css'

const App = () => {

  const [res, setRes] = useState(0)

  const increase =()=>{
    setRes(res + 1 )
  }
  const decrease =()=>{
    if (res>0){
    setRes(res - 1)
    }
    else
    {setRes(0)
    alert('You can not go below Zero')
    }
  }

  return (
    <>
      <div className="App-header">
         <div className="child1">
           <div className="countingResult">{res}</div>
           <hr />
           <div className="buttons">
             <button onClick={increase} className="positiveButton">Increase</button>
             <button onClick={decrease} className="negativeButton">Decrease</button>
           </div>
         </div>
       </div>
    </>
  )
}

export default App















